<template>
  <div class="session">
    
    <div class= "scene-container"  v-bind:class="{flashing: isFlashing}">
      <div id="scene_start" sceneNumber="1" class = "scene" v-bind:class="{currentScene: showScene1}">
        <div class = "sceneLabel"> trial start scene </div>
        <button v-on:click="startTrial_onclick" class= "nosepokeLarge" v-bind:class="{nosepokeLargeOff: ITIOn}"></button>
      </div>
      <div id="scene_task"  sceneNumber="2" class = "scene" v-bind:class="{currentScene: showScene2}" >
          <div class = "sceneLabel"> stimulus presentation scene </div>
          <div class= "nosepokeholescontainer">
            <button v-on:click="nosepoke1_onclick" class="nosepoke nosepoke1"  ></button>
            <button v-on:click="nosepoke2_onclick" class="nosepoke nosepoke2"  ></button>
            <button v-on:click="nosepoke3_onclick" class="nosepoke nosepoke3"  ></button>
            <button v-on:click="nosepoke4_onclick" class="nosepoke nosepoke4"  ></button>
          </div>
      </div>
      <div id="scene_win" class = "scene" sceneNumber="3" v-bind:class="{currentScene: showScene3}" >
          <div class = "sceneLabel"> win outcome scene</div>
      </div>
      <div id="scene_lose"  class = "scene" sceneNumber="4" v-bind:class="{currentScene: showScene4}" > 
          <div class = "sceneLabel"> win outcome scene</div>
      </div>
    </div>
    
  </div>
</template>

<script>
export default {
  name: "expDD",
  sockets: {
    connect: function() {
      console.log("session socket connected");
    },

    client_experiment_session_start: function(val) {
      this.config = val;
      this.running = true;
      console.log("client_experiment_session_start");
    },

    client_experiment_session_stop: function(val) {
      this.running = false;
      console.log("client_experiment_session_stop");
    },

    client_experiment_action: function(action) {
      console.log("client_experment_action", action);

      if (action.type.startsWith("ChangeToScene")) {
        this.showScene1 = false;
        this.showScene2 = false;
        this.showScene3 = false;
        this.showScene4 = false;
        this.showScene5 = false;

        switch (action.type) {
          case "ChangeToScene_start":
            this.currentScene = 1;
            this.showScene1 = true;
            this.ITIOn = false;
            break;
          case "ChangeToScene_task":
            this.currentScene = 2;
            this.showScene2 = true;
            break;
          case "ChangeToScene_win":
            this.currentScene = 3;
            this.showScene3 = true;
            beep();
            break;
          case "ChangeToScene_lose":
            this.currentScene = 4;
            this.showScene4 = true;
            break;
      }
    }
  },
  data() {
    return {
      config: {
        id: "Placeholder"
      },
      ruunning: false,
      currentScene: 0,
      showScene1: false,
      showScene2: false,
      showScene3: false,
      showScene4: false
     
    };
  },
  mounted() {
    console.log("mounted");
    this.$socket.emit("client_experiment_onevent", { type: "UI_onReady" });
  },
  methods: {
    event: function(actionType) {
      this.$socket.emit("client_experiment_onevent", { type: actionType });
    },

    startTrial_onclick: function() {
      this.event("startTrial_pressed");
    },
    nosepoke1_onclick: function() {
      this.event("nosePoke1_pressed");
    },
    nosepoke2_onclick: function() {
      this.event("nosePoke2_pressed");
    },
    nosepoke3_onclick: function() {
      this.event("nosePoke3_pressed");
    },
    nosepoke4_onclick: function() {
      this.event("nosePoke4_pressed");
    },
    beep: function () {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
    }
  }
}
};
</script>

